import React from "react";
import "./Button.css"

type Color = "active" | "default" | "disabled";

type Button = {
    text: string,
    buttonType?: "secondary",
    backgroundSubtype?: Color
}


export default function Button(Props: Button) {

    const { text, buttonType, backgroundSubtype } = Props;

    const changeStyle = () => {
        if (buttonType === "secondary") {
            const el: HTMLElement | null = document.getElementById("bgColor");

            // @ts-ignore: Object is possibly 'null'.
            el.style.backgroundColor = "white";

        } else {
            return null;
        }
    }



    return (
        <button className="bgColor" id="bgColor" data-subtype={backgroundSubtype}> { text }</button >
    )
}
